    private void btThemActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btThemActionPerformed
        try {
            int eff = NhaCungCapBUS.insert(getView());
            if(eff == 0) throw new RuntimeException();
            fillTable();
        } catch (RuntimeException | SQLException ex) {
            System.err.println(ex.toString());
            Mode.WMessage(this, "Error try to insert NhaCungCap");
        }
    }//GEN-LAST:event_btThemActionPerformed

    private void btSuaActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btSuaActionPerformed
        try {
            int eff = NhaCungCapBUS.update(getView());
            if(eff == 0) throw new RuntimeException();
            fillTable();
        } catch (RuntimeException | SQLException ex) {
            System.err.println(ex.toString());
            Mode.WMessage(this, "Error try to update NhaCungCap");
        }
    }//GEN-LAST:event_btSuaActionPerformed

    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        try {
            if(Mode.WConfirm(this, "Are you sure to delete this NhaCungCap") != JOptionPane.YES_OPTION) return;
            int eff = NhaCungCapBUS.delete(getView().getMaNCC());
            if(eff == 0) throw new RuntimeException();
            fillTable();
        } catch (RuntimeException | SQLException ex) {
            System.err.println(ex.toString());
            Mode.WMessage(this, "Error try to delete NhaCungCap");
        }
    }//GEN-LAST:event_jButton1ActionPerformed

