    private void fillTable() {
        try {
            fillTable(NhaCungCapBUS.select());
        } catch (SQLException ex) {
            System.err.println(ex.toString());
            Mode.WMessage(this, "Error try to query TaiKhoan");
        }
    }
    private void fillTable(List<NhaCungCapDTO> list){
        model = (DefaultTableModel) tblNhaCungCap.getModel();
        model.setRowCount(0);
        for(NhaCungCapDTO e : list){
            Object[] row = {e.getMaNCC(), e.getTenNCC(), e.getSdt(), e.getDiaChi(), e.getEmail()};
            model.addRow(row);
        }
        tblNhaCungCap.setModel(model);
    }
    
    private void viewDetail(){
        final int i = tblNhaCungCap.getSelectedRow();
        txtMa.setText(model.getValueAt(i,0).toString());
        txtTen.setText(model.getValueAt(i,1).toString());
        txtSDT.setText(model.getValueAt(i,2).toString());
        txtDiaChi.setText(model.getValueAt(i,3).toString());
        txtEmail.setText(model.getValueAt(i,5).toString());
    }
    
    private NhaCungCapDTO getView(){
        NhaCungCapDTO ncc = new NhaCungCapDTO();
        ncc.setMaNCC(txtMa.getText().trim());
        ncc.setTenNCC(txtTen.getText().trim());
        ncc.setSdt(txtSDT.getText().trim());
        ncc.setDiaChi(txtDiaChi.getText().trim());
        ncc.setEmail(txtEmail.getText().trim());
        return ncc;
    }
    
